package StringPackage;

public class CountOccurenceCharacter7 {

	public static void main(String[] args) {
				// TODO Auto-generated method stub
		
		String s = "asihik";
		char[] ch=s.toCharArray();
		char c='i';
		
	int count = 0;
	
		
		for(int j=0;j<ch.length;j++)
		{
			
			if(ch[j]==c)
			{
				count ++;
			}
		
	
	
	}
	System.out.println(count);
	}
	

}
